U  /**   * Tracking deeplink URL.   */  var productURL:String = _productURL  /**   * Product name.   */  var productName:String = _productName  /**   * Brand name.   */  var brandName:String = _brandName  /**   * Product description.   */  var description:String = _description  /**   * Price of the product.   */  var onlinePrice:Float = _onlinePrice  /**   * Three-letter currency code.   */  var currency:String = _currency  /**   * HTML-safe currency symbol.   */  var currencySymbol:String = _currencySymbol  /**   * URL of the product image.   */  var imageURL:String = _imageURL  /**   * ID of programme this product belongs to   */  var programmeId:Int = _programmeId  /**   * Name of programme this product belongs to   */  var programmeName:String = _programmeName  /**   * URL of programme this product belongs to   */  var programmeURL:String = _programmeURL  /**   * ID of level 1 category this product belongs to   */  var level1CategoryId:Int = _level1CategoryId  /**   * Name of level 1 category this product belongs to   */  var level1CategoryName:String = _level1CategoryName  /**   * ID of level 2 category this product belongs to   */  var level2CategoryId:Int = _level2CategoryId  /**   * Name of level 2 category this product belongs to   */  var level2CategoryName:String = _level2CategoryName  /**   * ID of feed this product belongs to   */  var feedId:Int = _feedId  /**   * Name of feed this product belongs to   */  var feedName:String = _feedName}